import React, { useState, useEffect } from 'react';
import './App.css';

const API = "https://apis.ccbp.in/list-creation/lists";

// Function to fetch data from API
const fetchUsers = async (url) => {
  try {
    const response = await fetch(url);
  const data = await response.json();
  return data.lists;
} catch (err) {
    console.error(err);
    return [];
  }
};

function App() {
  const [data, setData] = useState([]); // State to store list data
  const [list1, setList1] = useState([]); // List 1
  const [list2, setList2] = useState([]); // List 2
  const [middleList, setMiddleList] = useState([]); // Middle list that will be created dynamically
  const [loading, setLoading] = useState(true); // Loading state
  const [isListCreated, setIsListCreated] = useState(false); // Flag to check if middle list is created
  const [selectedLists, setSelectedLists] = useState({ list1: false, list2: false }); // Checkbox state

  // Fetch data when the component mounts
  useEffect(() => {
    const loadData = async () => {
      const fetchedData = await fetchUsers(API);
      setData(fetchedData); // Store fetched data in state
      setList1(fetchedData.filter(item => item.list_number === 1));
      setList2(fetchedData.filter(item => item.list_number === 2));
      setLoading(false);
    };
    loadData();
  }, []);

  // Handle checkbox change
  const handleCheckboxChange = (listNumber) => {
    setSelectedLists(prevState => ({
      ...prevState,
      [`list${listNumber}`]: !prevState[`list${listNumber}`],
    }));
  };

  // Create middle list when button is clicked
  const handleCreateList = () => {
    if (selectedLists.list1 && selectedLists.list2) {
      setIsListCreated(true); // Show the middle list
    } else {
      alert("Please select both List 1 and List 2.");
    }
  };

  // Handle moving items to the middle list
  const moveItemToMiddleList = (item, fromList) => {
    if (fromList === "list1") {
      setList1(list1.filter(i => i.id !== item.id));
    } else {
      setList2(list2.filter(i => i.id !== item.id));
    }
    setMiddleList(prevState => [...prevState, item]);
  };

  // Handle moving items back to the original lists
  const moveItemBackToList = (item, toList) => {
    setMiddleList(middleList.filter(i => i.id !== item.id));

    if (toList === "list1") {
      setList1(prevState => [...prevState, item]);
    } else {
      setList2(prevState => [...prevState, item]);
    }
  };

  // If loading, display loading message
  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1 className="header">List Creation</h1>

      {/* Create List Button */}
      <div className="create-list-button-container">
        <button className="create-list-button" onClick={handleCreateList}>
          Create List
        </button>
      </div>

      {/* Checkbox options */}
      <div className="lists-container">
        <div className="list-container">
          <label>
            <input 
              type="checkbox" 
              checked={selectedLists.list1} 
              onChange={() => handleCheckboxChange(1)} 
            />
            <span>List 1</span>
          </label>
          <div className="scrollable-list">
            <ul>
              {list1.map((item) => (
                <li key={item.id} onClick={() => moveItemToMiddleList(item, "list1")}>
                  <h3>{item.name}</h3>
                  <p>{item.description}</p>
                </li>
              ))}
            </ul>
          </div>
        </div>

        {/* Middle List */}
        {isListCreated && (
          <div className="middle-list-container">
            <span>Middle List</span>
            <div className="scrollable-list">
              <ul>
                {middleList.map((item) => (
                  <li key={item.id} onClick={() => moveItemBackToList(item, item.list_number === 1 ? "list1" : "list2")}>
                    <h3>{item.name}</h3>
                    <p>{item.description}</p>
                  </li>
                ))}
              </ul>
            </div>
          </div>
        )}

        <div className="list-container">
          <label>
            <input 
              type="checkbox" 
              checked={selectedLists.list2} 
              onChange={() => handleCheckboxChange(2)} 
            />
            <span>List 2</span>
          </label>
          <div className="scrollable-list">
            <ul>
              {list2.map((item) => (
                <li key={item.id} onClick={() => moveItemToMiddleList(item, "list2")}>
                  <h3>{item.name}</h3>
                  <p>{item.description}</p>
                </li>
              ))}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
