import React, { useState, useEffect } from 'react';
import './App.css';

const API = "https://apis.ccbp.in/list-creation/lists";

// Function to fetch users data
const fetchUsers = async (url) => {
  try {
    const response = await fetch(url);
    const data = await response.json();
    return data;
  } catch (err) {
    console.error(err);
  }
};

function App() {
  const [data, setData] = useState(null); // State to store fetched data
  const [loading, setLoading] = useState(true); // State to handle loading
  const [error, setError] = useState(null); // State to handle errors
  const [showList1, setShowList1] = useState(true); // State to control visibility of List 1
  const [showList2, setShowList2] = useState(true); // State to control visibility of List 2

  useEffect(() => {
    // Fetch data when the component mounts
    const loadData = async () => {
      try {
        const fetchedData = await fetchUsers(API);
        setData(fetchedData); // Store data in state
      } catch (err) {
        setError(err); // Handle errors if any
      } finally {
        setLoading(false); // Set loading to false once the data is fetched
      }
    };

    loadData(); // Call the async function
  }, []); // Empty dependency array means this effect runs only once when the component mounts

  // Handling loading and error states
  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error.message}</div>;
  }

  // Splitting the data based on list_number
  const list1 = data.lists.filter((item) => item.list_number === 1);
  const list2 = data.lists.filter((item) => item.list_number === 2);

  return (
    <div>
      <h1 className="header">List Creation</h1>
      <div className="checkbox-container">
        <label>
          <input
            type="checkbox"
            checked={showList1}
            onChange={() => setShowList1(!showList1)}
          />
          Show List 1
        </label>
        <label>
          <input
            type="checkbox"
            checked={showList2}
            onChange={() => setShowList2(!showList2)}
          />
          Show List 2
        </label>
      </div>

      <div className="lists-container">
        {showList1 && (
          <div className="list">
            <h2>List 1</h2>
            <ul>
              {list1.map((item) => (
                <li key={item.id}>
                  <h3>{item.name}</h3>
                  <p>{item.description}</p>
                </li>
              ))}
            </ul>
          </div>
        )}

        {showList2 && (
          <div className="list">
            <h2>List 2</h2>
            <ul>
              {list2.map((item) => (
                <li key={item.id}>
                  <h3>{item.name}</h3>
                  <p>{item.description}</p>
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
